dict.string:
#!/bin/sh
{ osascript -e "tell app \"$(basename "$TM_APP_PATH")\" to quit"
while ps >/dev/null -xp "$PPID"; do if (( ++n == 10 )); then "$DIALOG" </dev/null alert --title "Relaunch Timed Out" --body "Unable to exit TextMate." --button1 OK exit fi sleep .2; done
open "$TM_APP_PATH" --args -disableSessionRestore NO
} &>/dev/null &
